import React, { useState, useEffect, useCallback } from 'react';
import { Brain, Lightbulb, TrendingUp, Shield, DollarSign, Zap, CheckCircle } from 'lucide-react';

const AIArchitectureSuggestions = ({ formData, onApplySuggestion }) => {
  const [suggestions, setSuggestions] = useState([]);
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  const generateSuggestions = useCallback(async () => {
    setIsAnalyzing(true);

    // Simulate AI analysis
    await new Promise(resolve => setTimeout(resolve, 2000));

    const newSuggestions = [];

    // Analyze project type and suggest optimizations
    if (formData.projectName?.toLowerCase().includes('ecommerce')) {
      newSuggestions.push({
        id: 'ecommerce-optimization',
        type: 'performance',
        title: 'E-commerce Performance Optimization',
        description: 'Add CloudFront CDN and ElastiCache for better performance and user experience',
        impact: 'High',
        savings: '$45/month',
        confidence: 95,
        icon: <TrendingUp className="w-5 h-5 text-blue-500" />,
        recommendations: [
          'Add CloudFront CDN for global content delivery',
          'Implement ElastiCache for session management',
          'Use Lambda@Edge for dynamic content optimization'
        ],
        applyData: {
          selectedIntegration: [...(formData.selectedIntegration || []), 'cloudfront-cdn'],
          selectedOptimization: [...(formData.selectedOptimization || []), 'elasticache']
        }
      });
    }

    // Security suggestions
    if (!formData.selectedSecurity?.includes('waf-protection')) {
      newSuggestions.push({
        id: 'security-enhancement',
        type: 'security',
        title: 'Enhanced Security Posture',
        description: 'Add WAF protection and GuardDuty for comprehensive security monitoring',
        impact: 'Critical',
        savings: 'Risk Reduction',
        confidence: 98,
        icon: <Shield className="w-5 h-5 text-red-500" />,
        recommendations: [
          'Enable AWS WAF for application protection',
          'Add GuardDuty for threat detection',
          'Implement AWS Config for compliance monitoring'
        ],
        applyData: {
          selectedSecurity: [...(formData.selectedSecurity || []), 'waf-protection', 'guardduty']
        }
      });
    }

    // Cost optimization
    if (formData.selectedCompute?.includes('ec2-instances') && 
        !formData.selectedOptimization?.includes('auto-scaling')) {
      newSuggestions.push({
        id: 'cost-optimization',
        type: 'cost',
        title: 'Auto-Scaling Cost Optimization',
        description: 'Implement auto-scaling to reduce costs during low-traffic periods',
        impact: 'Medium',
        savings: '$120/month',
        confidence: 87,
        icon: <DollarSign className="w-5 h-5 text-green-500" />,
        recommendations: [
          'Enable EC2 Auto Scaling Groups',
          'Configure CloudWatch-based scaling policies',
          'Use Spot Instances for non-critical workloads'
        ],
        applyData: {
          selectedOptimization: [...(formData.selectedOptimization || []), 'auto-scaling', 'spot-instances']
        }
      });
    }

    // Serverless-first suggestion
    if (formData.selectedCompute?.includes('ec2-instances') && 
        !formData.selectedCompute?.includes('lambda-functions')) {
      newSuggestions.push({
        id: 'serverless-migration',
        type: 'architecture',
        title: 'Serverless-First Architecture',
        description: 'Migrate to Lambda functions for better scalability and cost efficiency',
        impact: 'High',
        savings: '$200/month',
        confidence: 92,
        icon: <Zap className="w-5 h-5 text-aws-orange" />,
        recommendations: [
          'Replace EC2 instances with Lambda functions',
          'Use API Gateway for serverless APIs',
          'Implement Step Functions for workflow orchestration'
        ],
        applyData: {
          selectedCompute: [...(formData.selectedCompute || []).filter(c => c !== 'ec2-instances'), 'lambda-functions'],
          selectedIntegration: [...(formData.selectedIntegration || []), 'api-gateway', 'step-functions']
        }
      });
    }

    // Monitoring enhancement
    if (!formData.selectedMonitoring?.includes('x-ray-tracing')) {
      newSuggestions.push({
        id: 'monitoring-enhancement',
        type: 'monitoring',
        title: 'Advanced Monitoring & Observability',
        description: 'Add X-Ray tracing and enhanced CloudWatch monitoring for better insights',
        impact: 'Medium',
        savings: 'Operational Efficiency',
        confidence: 89,
        icon: <Brain className="w-5 h-5 text-purple-500" />,
        recommendations: [
          'Enable AWS X-Ray for distributed tracing',
          'Set up custom CloudWatch dashboards',
          'Configure proactive alerting with SNS'
        ],
        applyData: {
          selectedMonitoring: [...(formData.selectedMonitoring || []), 'x-ray-tracing', 'custom-dashboards'],
          selectedIntegration: [...(formData.selectedIntegration || []), 'sns-notifications']
        }
      });
    }

    setSuggestions(newSuggestions);
    setIsAnalyzing(false);
  }, [formData]);

  useEffect(() => {
    if (formData.projectName || formData.selectedInfrastructure?.length > 0) {
      generateSuggestions();
    }
  }, [formData, generateSuggestions]);

  const getImpactColor = (impact) => {
    switch (impact) {
      case 'Critical': return 'text-red-600 bg-red-50 border-red-200';
      case 'High': return 'text-orange-600 bg-orange-50 border-orange-200';
      case 'Medium': return 'text-blue-600 bg-blue-50 border-blue-200';
      default: return 'text-gray-600 bg-gray-50 border-gray-200';
    }
  };

  const getTypeIcon = (type) => {
    switch (type) {
      case 'security': return <Shield className="w-4 h-4" />;
      case 'cost': return <DollarSign className="w-4 h-4" />;
      case 'performance': return <TrendingUp className="w-4 h-4" />;
      case 'architecture': return <Zap className="w-4 h-4" />;
      case 'monitoring': return <Brain className="w-4 h-4" />;
      default: return <Lightbulb className="w-4 h-4" />;
    }
  };

  if (suggestions.length === 0 && !isAnalyzing) {
    return null;
  }

  return (
    <div className="bg-white border border-aws-gray-200 rounded-lg p-6 mb-6">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold text-aws-blue flex items-center">
          <Brain className="w-5 h-5 mr-2 text-aws-orange" />
          AI Architecture Suggestions
        </h3>
        {isAnalyzing && (
          <div className="flex items-center text-sm text-aws-gray-600">
            <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-aws-orange mr-2"></div>
            Analyzing...
          </div>
        )}
      </div>

      {isAnalyzing ? (
        <div className="text-center py-8">
          <div className="animate-pulse">
            <Brain className="w-12 h-12 text-aws-orange mx-auto mb-4" />
            <p className="text-aws-gray-600">AI is analyzing your architecture...</p>
          </div>
        </div>
      ) : (
        <div className="space-y-4">
          {suggestions.map((suggestion) => (
            <div key={suggestion.id} className="border border-aws-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow duration-200">
              <div className="flex items-start justify-between mb-3">
                <div className="flex items-start space-x-3">
                  {suggestion.icon}
                  <div className="flex-1">
                    <h4 className="font-semibold text-aws-blue">{suggestion.title}</h4>
                    <p className="text-sm text-aws-gray-600 mt-1">{suggestion.description}</p>
                  </div>
                </div>
                <div className="flex items-center space-x-2">
                  <span className={`px-2 py-1 text-xs font-medium rounded border ${getImpactColor(suggestion.impact)}`}>
                    {suggestion.impact}
                  </span>
                  <div className="flex items-center text-xs text-aws-gray-500">
                    <CheckCircle className="w-3 h-3 mr-1" />
                    {suggestion.confidence}%
                  </div>
                </div>
              </div>

              <div className="mb-3">
                <h5 className="text-sm font-medium text-aws-gray-700 mb-2">Recommendations:</h5>
                <ul className="text-sm text-aws-gray-600 space-y-1">
                  {suggestion.recommendations.map((rec, index) => (
                    <li key={index} className="flex items-start">
                      <span className="text-aws-orange mr-2">â€¢</span>
                      {rec}
                    </li>
                  ))}
                </ul>
              </div>

              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-4 text-sm">
                  <div className="flex items-center text-green-600">
                    <DollarSign className="w-4 h-4 mr-1" />
                    <span>Saves: {suggestion.savings}</span>
                  </div>
                  <div className="flex items-center text-aws-gray-500">
                    {getTypeIcon(suggestion.type)}
                    <span className="ml-1 capitalize">{suggestion.type}</span>
                  </div>
                </div>
                <button
                  onClick={() => onApplySuggestion(suggestion.applyData)}
                  className="px-4 py-2 bg-aws-orange text-white text-sm font-medium rounded-lg hover:bg-aws-orange-dark transition-colors duration-200"
                >
                  Apply Suggestion
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AIArchitectureSuggestions;
