import React from 'react';
import { Check } from 'lucide-react';

const StepIndicator = ({ steps, currentStep }) => {
  return (
    <div className="bg-white border border-aws-gray-200 rounded-lg shadow-sm p-6 mb-6 overflow-x-auto">
      <div className="flex items-center justify-between min-w-max">
        {steps.map((step, index) => (
          <div key={step.id} className="flex items-center">
            {/* Step Circle and Label */}
            <div className="flex flex-col items-center relative">
              <div
                className={`step-indicator ${
                  step.id < currentStep
                    ? 'completed'
                    : step.id === currentStep
                    ? 'active'
                    : 'inactive'
                }`}
              >
                {step.id < currentStep ? (
                  <Check className="w-4 h-4" />
                ) : (
                  <span className="text-sm font-semibold">{step.id}</span>
                )}
              </div>
              <span
                className={`mt-3 text-sm font-semibold text-center whitespace-nowrap ${
                  step.id === currentStep
                    ? 'text-aws-orange'
                    : step.id < currentStep
                    ? 'text-green-600'
                    : 'text-aws-gray-700'
                }`}
              >
                {step.name}
              </span>
            </div>

            {/* Connection Line */}
            {index < steps.length - 1 && (
              <div className="flex-1 flex items-center justify-center mx-4">
                <div
                  className={`h-1 flex-1 ${
                    step.id < currentStep ? 'bg-green-500' : 'bg-aws-gray-300'
                  }`}
                  style={{ minWidth: '60px' }}
                />
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default StepIndicator;
